import React from 'react';
import { mount } from 'enzyme';
import AccordionSection from '..';
describe('<AccordionSection/>', function () {
  it('should have type button on section summary', function () {
    var component = mount(React.createElement(AccordionSection, {
      label: "Test section"
    }));
    expect(component.find('button').prop('type')).toBe('button');
  });
});