import React from 'react';
import { mount } from 'enzyme';
import Indicators from '../indicators';
var carouselItems = [{
  indicatorID: 'indicator-1',
  containerID: 'container-1',
  ref: {
    click: jest.fn()
  },
  header: 'Header'
}, {
  indicatorID: 'indicator-2',
  containerID: 'container-2',
  ref: {
    click: jest.fn()
  },
  header: 'Header 2'
}];
describe('<Indicators />', function () {
  it('should render the right amount of indicators', function () {
    var component = mount(React.createElement(Indicators, {
      carouselChildren: carouselItems
    }));
    expect(component.find('li').length).toBe(2);
  });
  it('should set the role as tablist in ul element', function () {
    var component = mount(React.createElement(Indicators, {
      carouselChildren: carouselItems
    }));
    expect(component.find('ul').prop('role')).toBe('tablist');
  });
});